C++<pre><code>vector&lt;string&gt; split(const string&amp; s)
{
    vector&lt;string&gt; ret;
    typedef string::size_def string_size;
    string_size i = 0;
    while (i != s.size()) {
        while (i != s.size() &amp;&amp; isspace(s[i])) // is from &lt;cctype&gt;
            ++i;
        string_size j = i;
        while (j != s.size() &amp;&amp; !isspace(s[i]))
            ++j;
        if (i != j) {
            ret.push_back(s.substr(i, j - i));
            i = j;
        }
    }
    return ret;
}
</code></pre>

