Bash<pre><code># 自动解压：判断文件后缀名并调用相应解压命令
function q-extract() {
    if [ -f $1 ] ; then
        case $1 in
        *.tar.bz2)   tar -xvjf $1    ;;
        *.tar.gz)    tar -xvzf $1    ;;
        *.tar.xz)    tar -xvJf $1    ;;
        *.bz2)       bunzip2 $1     ;;
        *.rar)       rar x $1       ;;
        *.gz)        gunzip $1      ;;
        *.tar)       tar -xvf $1     ;;
        *.tbz2)      tar -xvjf $1    ;;
        *.tgz)       tar -xvzf $1    ;;
        *.zip)       unzip $1       ;;
        *.Z)         uncompress $1  ;;
        *.7z)        7z x $1        ;;
        *)           echo "don't know how to extract '$1'..." ;;
        esac
    else
        echo "'$1' is not a valid file!"
    fi
}

# 自动压缩：判断后缀名并调用相应压缩程序
function q-compress() {
    if [ -n "$1" ] ; then
        FILE=$1
        case $FILE in
        *.tar) shift &amp;&amp; tar -cf $FILE $* ;;
        *.tar.bz2) shift &amp;&amp; tar -cjf $FILE $* ;;
        *.tar.xz) shift &amp;&amp; tar -cJf $FILE $* ;;
        *.tar.gz) shift &amp;&amp; tar -czf $FILE $* ;;
        *.tgz) shift &amp;&amp; tar -czf $FILE $* ;;
        *.zip) shift &amp;&amp; zip $FILE $* ;;
        *.rar) shift &amp;&amp; rar $FILE $* ;;
        esac
    else
        echo "usage: q-compress &lt;foo.tar.gz&gt; ./foo ./bar"
    fi
}

# 漂亮的带语法高亮的 color cat ，需要先 pip install pygments
function ccat() {
    local style="monokai"
    if [ $# -eq 0 ]; then
        pygmentize -P style=$style -P tabsize=4 -f terminal256 -g
    else
        for NAME in $@; do
            pygmentize -P style=$style -P tabsize=4 -f terminal256 -g "$NAME"
        done
    fi
}
